INSERT INTO `message_table` VALUES (17,'web前端开发','/uploads/fengmian/%E9%80%89%E5%8C%BA_001_ggGguUI.png','thinkgamer','2017-04-23 04:03:00.000000',1000,1,'',0,'<p>\r\n	<span style=\"font-size:16px;\">1：需要根据前段效果图进行切分实现</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">2：界面要美观大方得体</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">3：熟悉html，css，javascript，nodejs</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">4：拥有自主开发能力</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">5：具有解决问题的能力</span>\r\n</p>',4,'需求'),(20,'P图','/uploads/fengmian/110H41A643-1.jpg','thinkgamer','2017-04-23 09:33:00.000000',12,0,'',0,'氛围                 vvbbreberbrevbevevwe上的看法是哪款涉及到；个女的时间',1,'需求'),(21,'专门撰写各种文案策划','/uploads/fengmian/110HGG426-9.jpg','thinkgamer','2017-04-23 09:42:55.000000',1100,3,'',0,'射雕vsdvs的vsdvs',2,'需求'),(22,'提供P图服务','/uploads/fengmian/purple_color_burst-wallpaper-3554x1999.jpg','thinkgamer','2017-04-23 10:01:13.000000',20,6,'',0,'按方法三发发发发',1,'服务');
